    private void buttonSomaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonSomaActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(input01.getText());
        double num2 = Double.parseDouble(input02.getText());
        
        Soma s = new Soma();
        
        output.setText(String.valueOf(s.calcula(num1, num2)));
    }//GEN-LAST:event_buttonSomaActionPerformed

